let vert=550,horizon=1100;function rectum(){for(let e=0;e<10;e++){Math.random();let e=document.createElement("div");e.classList.add("rect"),e.style.top=vert*Math.random()+"px",e.style.left=horizon*Math.random()+"px",document.body.appendChild(e)}setTimeout(function(){document.querySelectorAll(".rect").forEach(e=>{e.classList.add("boom"),setTimeout(function(){e.remove()},1e3)})},1e3)}document.querySelector("button").addEventListener("click",()=>{rectum()});let xhr=new XMLHttpRequest;xhr.open("GET","data.json",!0),xhr.send(),xhr.onload=function(){4==xhr.readyState&&(document.body.innerHTML=xhr.responseText)};